async function submitModelUpdate(ctx, nodeId, updateHash, metadata) { // 1. Validate node identity
const node = await ctx.stub.getState(nodeId);
if (!node) throw new Error('Unauthorized node');
// 2. Check update format and size
if (!isValidHash(updateHash)) throw new Error('Invalid hash');
// 3. Record on blockchain
const txn = { nodeId, updateHash, timestamp: Date.now(), blockNumber: ctx.stub.getTxID() };
await ctx.stub.putState(updateHash, Buffer.from(JSON.stringify(txn)));
return txn;
}
